  color: var(--color-secondary-greyblue);
  position: relative;
}
.main .active::after {
  position: absolute;
  -webkit-transform: translate(-50%, -50%);
          transform: translate(-50%, -50%);
  content: "";
  background: var(--color-primary-red);
  bottom: -2px;
  left: 50%;
  height: 3px;
  width: 100%;
}
