    @include font-size(1.6rem);
    font-weight: 500;
    text-decoration: none;
    text-transform: capitalize;
    border: 2px solid transparent;
    border-radius: 8px;
    @include shadow;
    transition: 0.2s ease-in-out;
    @include respond-below(xl) {
      font-size: 1.4rem;
    }
    @include respond-below(xs) {
      padding: 1rem 1.6rem;
    }
    @media (max-width: 390px) {
      font-size: 1.3rem;
      padding: 1rem 1.4rem;
    }
  }
  &:hover,
  &:active {
    background-color: transparent;
    color: var(--color-primary-blue);
    border: 2px solid var(--color-primary-blue);
  }

  &__chrome,
  &__extension,
  &__ques {
    color: #f4f4f4;
    background-color: var(--color-primary-blue);
  }
  &__firefox {
    margin-left: 3rem;
    color: var(--color-secondary-darkblue);
    background-color: hsla(229, 8%, 60%, 0.2);
    @include respond-below(xs) {
      margin-left: 2rem;
    }

    @media (max-width: 390px) {
      margin-left: 1rem;
    }
  }

  &__extension {
    margin-top: 2rem;
    margin-bottom: 0;
  }

  &__ques {
    margin-top: 6rem;
  }
}

.main {
  & .feature-tab {
    position: relative;
    background-color: transparent;
    float: left;
    border: none;
    outline: none;
    cursor: pointer;
    padding: 1.4rem 3rem;
    transition: 0.3s;
    font-size: 17px;
    color: var(--color-secondary-grayblue);
    border-bottom: 1px solid var(--color-secondary-grayblue);

    &:first-child {
      @include respond-below(xs) {
        &::before {
          content: "";
          background: var(--color-secondary-grayblue);
          position: absolute;
          top: 0;
          left: 0;
          height: 1px;
          width: 100%;
        }
      }
    }

    &:hover,
    &:active {
      background-color: transparent;
      color: var(--color-primary-red);
    }
  }
  & .active {
    color: var(--color-secondary-greyblue);
    position: relative;
    &::after {
      position: absolute;
      transform: translate(-50%, -50%);
      content: "";
      background: var(--color-primary-red);
      bottom: -2px;
      left: 50%;
      height: 3px;
      width: 100%;
    }
    @include respond-below(xs) {
      &::after {
        width: 40%;
      }
    }
  }

  & .feature-btn {
    color: #f4f4f4;
    background-color: var(--color-primary-blue);

    @include respond-below(lg) {
      padding: 1.2rem 2.2rem;
    }

    &:hover,
    &:active {
      background-color: transparent;
      color: var(--color-primary-blue);
      border: 2px solid var(--color-primary-blue);
    }
  }
}

sources.src\\sass\\abstract\\_breakpoints.scss:
//
//  MEDIA QUERIES
//––––––––––––––––––––––––––––––––––––––––––––––––––

// A map of breakpoints.
$breakpoints: (
  xs: 576px,
  sm: 768px,
  md: 1024px,
  lg: 1200px,
  xl: 1800px
);

//
//  RESPOND ABOVE
//––––––––––––––––––––––––––––––––––––––––––––––––––

// @include respond-above(sm) {}
@mixin respond-above($breakpoint) {
  // If the breakpoint exists in the map.
  @if map-has-key($breakpoints, $breakpoint) {
    // Get the breakpoint value.
    $breakpoint-value: map-get($breakpoints, $breakpoint);

    // Write the media query.
    @media (min-width: $breakpoint-value) {
      @content;
    }

    // If the breakpoint doesn't exist in the map.
  } @else {
    // Log a warning.
    @warn 'Invalid breakpoint: #{$breakpoint}.';
  }
}

//
//  RESPOND BELOW
//––––––––––––––––––––––––––––––––––––––––––––––––––

// @include respond-below(sm) {}
@mixin respond-below($breakpoint) {
  // If the breakpoint exists in the map.
  @if map-has-key($breakpoints, $breakpoint) {
    // Get the breakpoint value.
    $breakpoint-value: map-get($breakpoints, $breakpoint);

    // Write the media query.
    @media (max-width: ($breakpoint-value - 1)) {
      @content;
    }

    // If the breakpoint doesn't exist in the map.
  } @else {
    // Log a warning.
    @warn 'Invalid breakpoint: #{$breakpoint}.';
  }
}

//
//  RESPOND BETWEEN
//––––––––––––––––––––––––––––––––––––––––––––––––––

// @include respond-between(sm, md) {}
@mixin respond-between($lower, $upper) {
  // If both the lower and upper breakpoints exist in the map.
  @if map-has-key($breakpoints, $lower) and map-has-key($breakpoints, $upper) {
    // Get the lower and upper breakpoints.
    $lower-breakpoint: map-get($breakpoints, $lower);
    $upper-breakpoint: map-get($breakpoints, $upper);

    // Write the media query.
    @media (min-width: $lower-breakpoint) and (max-width: ($upper-breakpoint - 1)) {
      @content;
    }

    // If one or both of the breakpoints don't exist.
  } @else {
    // If lower breakpoint is invalid.
    @if (map-has-key($breakpoints, $lower) == false) {
      // Log a warning.
      @warn 'Your lower breakpoint was invalid: #{$lower}.';
    }

    // If upper breakpoint is invalid.
    @if (map-has-key($breakpoints, $upper) == false) {
      // Log a warning.
      @warn 'Your upper breakpoint was invalid: #{$upper}.';
    }
  }
}

sources.src\\sass\\layout\\_header.scss:
//

.header {
  display: flex;
  align-items: center;
  margin-top: 12rem;

  @include respond-below(lg) {
    flex-direction: column;
  }
  @include respond-below(xs) {
    margin-top: 6rem;
  }

  & .col-1 {
    @include margin;
    margin-right: 0;
    display: flex;
    flex-direction: column;
    flex-basis: 40%;

    @include respond-below(lg) {
      order: 1;
      @include margin;
      text-align: center;
      padding-top: 2rem;
    }
  }

  & .col-2 {
    flex-shrink: 2;
    width: 100vw;
    display: flex;
    justify-content: center;
    & .hero-img {
      padding-left: 10%;
      width: 90%;
      height: auto;
      padding-bottom: 4rem;
      @include respond-below(lg) {
        width: 70%;
      }
      @include respond-below(md) {
        width: calc(100vw - 10%);
      }
      @include respond-below(xs) {
        width: 100%;
      }
    }

    @include bgShape;
    &::before {
      bottom: 0;
      right: 0;
      border-top-left-radius: 100rem;
      border-bottom-left-radius: 100rem;
    }
    @include respond-below(lg) {
      &::before {
        width: 60%;
        height: 65%;
        bottom: 10%;
      }
    }
    @include respond-below(md) {
      &::before {
        width: 65%;
        height: 65%;
        bottom: 10%;
      }
    }
    @include respond-below(sm) {
      &::before {
        width: 70%;
        height: 60%;
        bottom: 15%;
      }
    }
  }

  &__h1 {
    @include headertag;
    @include respond-below(xl) {
      font-size: 4rem;
    }
    @include respond-below(md) {
      font-size: 3.5rem;
    }
    @include respond-below(xs) {
      font-size: 3.2rem;
    }
  }

  &__para {
    color: var(--color-secondary-grayblue);
    line-height: 1.4;
    margin: 0;
  }

  &__buttons {
    margin-top: 5rem;
  }
}

sources.src\\sass\\layout\\_navigation.scss:
.navigation {
  position: relative;
  display: flex;
  justify-content: space-between;
  align-items: center;
  @include margin;
  margin-top: 5rem;

  &__logo {
    transform: scale(1.2);
    @include respond-below(sm) {
      transform: scale(1);
    }
  }

  .nav {
    &__links {
      display: flex;
      list-style-type: none;
    }

    &__item {
      &:nth-child(4) {
        padding-left: 2rem;
        .nav__link {
          &:link,
          &:visited {
            color: #ffffff;
            background-color: var(--color-primary-red);
            border-radius: 5px;
          }

          &:hover,
          &:active {
            color: var(--color-primary-red);
            background-color: transparent;
            border: 2px solid salmon;
          }
        }
      }
      @include respond-below(md) {
        display: none;
      }
      &:last-child {
        padding: 5px;
        display: none;
        @include respond-below(md) {
          display: initial;
        }
      }
    }

    &__link {
      &:link,
      &:visited {
        // display: inline-block;
        padding: 1rem 2.5rem;
        font-family: var(--font-primary);
        @include font-size(1.6rem);
        font-weight: 400;
        color: var(--color-secondary-darkblue);
        text-decoration: none;
        text-transform: uppercase;
        letter-spacing: 1px;
        border: 2px solid transparent;
      }

      &:hover,
      &:active {
        color: var(--color-primary-red);
      }
    }
  }
}

.menu {
  display: none;
  width: 100%;
  height: 100vh;
  position: absolute;
  z-index: 999;
  padding: 0 12%;
  padding-top: 1.6rem;
  margin-top: -5rem;
  background-color: rgba(37, 43, 70, 0.9);
  @include respond-below(xs) {
    margin-top: 0;
    padding: 0 8%;
  }
  &__logo {
    display: flex;
    justify-content: space-between;
    @include respond-below(md) {
      margin-top: 5rem;
    }
    @include respond-below(xs) {
      margin-top: 1.6rem;
    }

    & .close {
      padding: 5px;
    }
  }

  .menu__nav {
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-top: 10rem;
    text-align: center;
    &__links {
      display: flex;
      flex-direction: column;
      list-style-type: none;
      justify-content: center;
      text-align: center;
      padding: 0 08%;
      width: 100%;
    }

    &__social {
      display: flex;
      justify-content: center;
      align-items: center;
      list-style-type: none;

      & li {
        padding: 1rem 2rem;
      }
    }
    &__item {
      margin-bottom: 5rem;
      position: relative;

      &:last-child {
        border: 2px solid #ffffff;
        padding: 1rem;
      }
      &::before {
        content: "";
        background: var(--color-secondary-grayblue);
        position: absolute;
        top: -2.5rem;
        left: 0;
        height: 1px;
        width: 100%;
      }
    }

    &__link {
      &:link,
      &:visited {
        font-size: 2rem;
        padding: 1rem 2.5rem;
        font-family: var(--font-primary);

        font-weight: 400;
        color: var(--color-secondary-darkblue);
        text-decoration: none;
        text-transform: uppercase;
        letter-spacing: 1px;
        border: 2px solid transparent;
        color: #ffffff;
      }

      &:hover,
      &:active {
        color: var(--color-primary-red);
      }
    }
  }
}

sources.src\\sass\\layout\\_footer.scss:
.footer {
  margin-top: 15rem;

  & .row {
    text-align: center;
    background-color: var(--color-primary-blue);
    padding: 5rem 10% 10rem 10%;
    @include respond-below(sm) {
      padding-right: 8%;
      padding-left: 8%;
    }
    & h6 {
      font-size: 1.3rem;
      letter-spacing: 5px;
      color: #ffffffe0;
      font-weight: 500;
      text-transform: uppercase;
    }

    & h2 {
      font-size: 3.4rem;
      line-height: 1.4;
      margin-top: 4rem;
      color: white;
      font-weight: 500;
      width: 50rem;
      margin: 0 auto;

      @include respond-below(sm) {
        width: 80%;
      }
      @include respond-below(xs) {
        width: 100%;
      }
    }

    & .contact {
      margin-top: 4rem;

      & input {
        padding: 1.4rem 1.6rem;
        border: none;
        border-radius: 6px;
        margin-right: 1rem;
        width: 32rem;
        border: 2px solid transparent;
        @include respond-below(sm) {
          width: 80%;
          margin-right: 0;
        }
        @include respond-below(xs) {
          width: 100%;
        }
        &::placeholder {
          font-size: 1.5rem;
          color: rgba(128, 128, 128, 0.5);
        }
      }
      & button {
        background-color: transparent;
        border: none;
        outline: none;
        cursor: pointer;
        padding: 1.4rem 2.2rem;
        transition: 0.3s;
        font-size: 17px;
        color: #ffffff;
        border-radius: 6px;
        background-color: var(--color-primary-red);
        border: 2px solid transparent;
        @include respond-below(sm) {
          margin-top: 2rem;
          //   width: 100%;
        }
        @include respond-below(xs) {
          width: 100%;
        }

        &:hover {
          color: var(--color-primary-red);
          background-color: #fff;
          border: 2px solid var(--color-primary-red);
        }
      }
    }
  }

  &__links {
    display: flex;
    background-color: var(--color-secondary-darkblue);
    padding: 4rem 10%;
    justify-content: space-around;
    align-items: center;
    @include respond-below(sm) {
      flex-direction: column;
      justify-content: center;
    }
    & a {
      font-size: 1.3rem;
      font-weight: 400;
      letter-spacing: 3px;
      color: #ffffff;
      text-transform: uppercase;
      text-decoration: none;
      transition: 0.1s ease-in-out;
      @include respond-below(sm) {
        margin-bottom: 3rem;
        font-size: 1.4rem;
      }
      &:hover,
      &:active {
        fill: red;
        background-color: transparent;
        color: var(--color-primary-red);
      }

      &:nth-child(4) {
        margin-right: 50%;
        @include respond-below(xl) {
          margin-right: 35%;
        }
        @include respond-below(lg) {
          margin-right: 25%;
        }
        @include respond-below(md) {
          margin-right: 8%;
        }
        @include respond-below(sm) {
          margin-right: 0;
        }
      }
    }

    & .social {
      & a {
        margin: 0 2rem;
        fill: #ffffff;
        &:hover,
        &:active {
          fill: red;
          background-color: transparent;
          fill: var(--color-primary-red);
        }
      }
    }
  }
}

sources.src\\sass\\pages\\_home.scss:
.main {
  margin-top: 10rem;
}

.features {
  width: 100%;
  margin-top: 15rem;
  @include h2andpara;

  @include respond-below(xs) {
    &__para {
      width: 92%;
    }
  }
  &__features {
    margin-top: 8rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
  }

  & .features-tabs {
    @include respond-below(xs) {
      margin: 0 8%;
    }
  }

  & .feature-tab {
    @include respond-below(xs) {
      width: 100%;
      display: block;
    }
  }

  & .feature-info {
    display: flex;
    width: 100%;
    margin-top: 10rem;

    @include respond-below(sm) {
      flex-direction: column;
    }

    & .col-1 {
      flex-basis: 50%;
      display: flex;
      justify-content: flex-end;
      @include bgShape;

      @include respond-below(lg) {
        width: 100%;
        justify-content: center;
        margin-bottom: 10rem;
      }
      &::before {
        width: 90%;
        height: 90%;
        bottom: -9rem;
        left: 0;
        border-top-right-radius: 100rem;
        border-bottom-right-radius: 100rem;

        @include respond-below(lg) {
          bottom: -8rem;
        }
        @include respond-below(md) {
          width: 85%;
          height: 100%;
          bottom: -7rem;
        }
        @include respond-below(md) {
          width: 85%;
          height: 100%;
          bottom: -7rem;
        }
      }

      & .fe-img {
        width: 70%;
        @include respond-below(lg) {
          // width: 90%;
        }
      }
    }

    & .col-2 {
      flex-basis: 40%;
      margin-left: 14rem;
      display: flex;
      flex-direction: column;
      align-items: flex-start;
      @include respond-below(lg) {
        margin-left: 8rem;
      }
      @include respond-below(md) {
        margin: 0 8%;
        align-items: center;
      }
      & .feature__desc-h2 {
        @include headertag;
        font-size: 3.5rem;
        @include respond-below(lg) {
          font-size: 3rem;
        }
        @include respond-below(md) {
          font-size: 2.5rem;
        }
        @include respond-below(sm) {
          font-size: 3rem;
        }
        @include respond-below(xs) {
          font-size: 2.5rem;
        }
      }
      & .feature__desc-para {
        width: 64%;
        line-height: 1.6;
        color: var(--color-secondary-grayblue);
        margin-bottom: 4rem;
        @include respond-below(lg) {
          width: 80%;
        }
        @include respond-below(md) {
          width: 80%;
        }
        @include respond-below(sm) {
          width: 100%;
        }
      }
    }
  }
}

.extension {
  @include margin;
  margin-top: 30rem;
  @include respond-below(xl) {
    margin-top: 20rem;
  }
  @include respond-below(lg) {
    margin-top: 15rem;
  }
  @include respond-below(xs) {
    margin-top: 8rem;
  }
  @include h2andpara;
  &__tab {
    display: flex;
    align-items: flex-start;
    justify-content: center;
    margin-top: 10rem;

    @include respond-below(lg) {
      flex-wrap: wrap;
    }
    @include respond-below(sm) {
      flex-direction: column;
      align-items: center;
    }
  }

  & .browser-ext {
    display: flex;
    align-items: center;
    flex-direction: column;
    margin: 0 2rem;
    padding: 3rem 1rem;
    border-radius: 1.6rem;
    box-shadow: 0 12px 20px -6px rgba(83, 104, 223, 0.2);
    overflow: hidden;

    @include respond-below(sm) {
      width: auto;
    }
  
    & .browser-logo {
      align-self: center;
      width: 10rem;
      height: 10rem;
    }
    & h3 {
      margin-top: 3rem;
      margin-bottom: 0;
      font-size: 2.4rem;
      font-weight: 500;
      color: var(--color-secondary-darkblue);
      @include respond-below(lg) {
        font-size: 1.8rem;
      }
    }
    & p {
      margin-top: 1.5rem;
      margin-bottom: 4rem;
      font-size: 1.6rem;
      color: var(--color-secondary-grayblue);
    }
    & img {
    }
    &:nth-child(2) {
      margin-top: 4rem;
      @include respond-below(lg) {
        margin-top: 0;
      }
      @include respond-below(xs) {
        margin-top: 8rem;
      }
    }
    &:nth-child(3) {
      margin-top: 8rem;
      @include respond-below(lg) {
        margin-top: 4rem;
      }
      @include respond-below(xs) {
        margin-top: 8rem;
      }
    }
  }
}

.ques {
  @include margin;
  margin-top: 15rem;
  @include respond-below(xs) {
    margin-top: 8rem;
  }
  @include h2andpara;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

  &__list {
    margin-top: 10rem;
    width: 100%;
  }

  .faq {
    width: 54rem;
    margin: 0 auto;
    padding: 2.5rem 0;
    position: relative;
    cursor: pointer;
    // @include respond-below(lg) {
    //   width: 100%;
    // }
    @include respond-below(xs) {
      width: 100%;
    }
    &:hover {
      .faq__ques {
        color: var(--color-primary-red);
        transition: 0.1s ease;
      }
    }
    &:first-child::before {
      content: "";
      width: 100%;
      height: 1px;
      background-color: rgba(145, 148, 161, 0.4);
      top: 0;
      right: 0;
      position: absolute;
    }
    &::after {
      content: "";
      width: 100%;
      height: 1px;
      background-color: rgba(145, 148, 161, 0.4);
      bottom: 0;
      right: 0;
      position: absolute;
    }
    &__ques {
      // display: block;
      padding-top: 2rem;
      padding-bottom: 2rem;
      display: inline;
      font-size: 2rem;
      font-weight: 400;
      color: var(--color-secondary-darkblue);
      @include respond-below(xs) {
        font-size: 1.8rem;
      }
    }
    &__arrow {
      position: absolute;
      right: 2rem;
      top: calc(2.5rem + 6px);
      @include respond-below(xs) {
        right: -2px;
      }
    }
    .arrow-stroke {
      path {
        stroke: var(--color-primary-red);
      }
    }
    &__ans {
      margin-top: 4rem;
      color: var(--color-secondary-grayblue);
      line-height: 1.8;
    }
  }
}
