  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  min-height: 100vh;
  position: relative;
  background-color: var(--color-gray);
}

.container {
  max-width: 72rem;
  display: flex;
  flex-wrap: wrap;
  font-size: 1.6rem;
  box-shadow: 20px 20px 50px 15px rgba(128, 128, 128, 0.4);
  border-radius: 10px;
  background-color: #ffffffcc;
}
@media only screen and (max-width: 699px) {
  .container {
    flex-direction: column;
    flex-wrap: nowrap;
  }
}
.container__info {
  flex-basis: 100%;
  padding: 2rem 4rem;
}
.container__info--heading {
  color: var(--color-primary);
  font-size: 3rem;
}
.container__info h3 {
  color: var(--color-secodary);
  font-size: 2rem;
}
.container__info .info__paragraph {
  color: var(--color-gray-blue);
  font-size: 1.8rem;
  line-height: 1.6;
}
.container__plan {
  padding: 4rem;
  flex-basis: calc(50% - 8rem);
  color: var(--color-gray);
  background-color: var(--color-primary);
  border-bottom-left-radius: inherit;
}
@media only screen and (max-width: 699px) {
  .container__plan {
    border-radius: unset;
  }
}
.container__plan .plan__sub {
  display: flex;
  align-items: center;
  color: var(--color-gray-light);
}
.container__plan .plan__price {
  font-size: 4rem;
  padding-right: 1.2rem;
  color: var(--color-gray);
}
.container__plan .plan__para {
  margin: 5px 0 30px 0;
}
.container__desc {
  position: relative;
  padding: 4rem;
  background-color: rgba(42, 178, 175, 0.88);
  border-bottom-right-radius: inherit;
  line-height: 1.6;
  flex-basis: calc(50% - 8rem);
  color: var(--color-gray);
}
@media only screen and (max-width: 699px) {
  .container__desc {
    border-radius: unset;
  }
}
.container__desc .desc__para {
  color: rgba(229, 238, 244, 0.9);
}

footer {
  position: absolute;
  bottom: 0;
}
@media only screen and (max-width: 835px) {
  footer {
    position: unset;
  }
}
footer .attribution {
  font-size: 1.4rem;
  color: var(--color-primary);
}
footer a, footer a:link, footer a:visited {
  text-transform: capitalize;
  text-decoration: none;
  color: rgba(29, 28, 28, 0.8);
  border-bottom: 1px dotted rgba(29, 28, 28, 0.8);
}

